package com.mobile_live_widgets_prod.myjavaservice4.controller;

import com.mobile_live_widgets_prod.myjavaservice4.MyJavaService4;
import java.lang.String;
import javax.servlet.http.HttpServletRequest;
import org.springframework.web.bind.annotation.*;
import org.springframework.http.MediaType;
import org.springframework.beans.factory.annotation.Autowired;

@RestController
@RequestMapping(value = "/myJavaService4")
public class MyJavaService4Controller {

    @Autowired
    private MyJavaService4 myJavaService4;

    @RequestMapping(value = "/sampleJavaOperation", produces = "application/json", method = RequestMethod.GET)
    public String sampleJavaOperation(@RequestParam(value = "name", required = false) String name, HttpServletRequest request) {
        return myJavaService4.sampleJavaOperation(name, request);
    }
}
